  }
  
  async addTodo() {
    const input = document.getElementById('todo-input');
    const text = input.value.trim();
    
    if (text === '') {
      AnimationUtils.shake(input);
      return;
    }
    
    const todo = {
      id: this.todoId++,
      text: text,
      completed: false,
      createdAt: new Date().toLocaleString()
    };
    
    this.todos.push(todo);
    input.value = '';
    this.updateCharCount(0);
    
    // ì„±ê³µ ì• ë‹ˆë©”ì´ì…˜
    const addBtn = document.getElementById('add-btn');
    AnimationUtils.bounce(addBtn);
    
    await this.renderTodos();
    this.updateStats();
    this.saveToStorage();
  }
  
  async deleteTodo(id) {
    const todoElement = document.querySelector(`[data-id="${id}"]`).closest('.todo-item');
    
    // ì‚­ì œ ì• ë‹ˆë©”ì´ì…˜
    todoElement.classList.add('removing');
    
    // ì• ë‹ˆë©”ì´ì…˜ ì™„ë£Œ í›„ ì‹¤ì œ ì‚­ì œ
    setTimeout(() => {
      this.todos = this.todos.filter(t => t.id !== id);
      this.renderTodos();
      this.updateStats();
      this.saveToStorage();
    }, 300);
  }
  
  toggleTodo(id) {
    const todo = this.todos.find(t => t.id === id);
    if (todo) {
      todo.completed = !todo.completed;
      
      // ì™„ë£Œ ì• ë‹ˆë©”ì´ì…˜
      const todoElement = document.querySelector(`[data-id="${id}"]`).closest('.todo-item');
      if (todo.completed) {
        AnimationUtils.bounce(todoElement);
      }
      
      this.renderTodos();
      this.updateStats();
      this.saveToStorage();
    }
  }
  
  async renderTodos() {
    const todoList = document.getElementById('todo-list');
    const emptyState = document.getElementById('empty-state');
    
    const filteredTodos = this.getFilteredTodos();
    
    if (filteredTodos.length === 0) {
      todoList.innerHTML = '';
      emptyState.classList.add('show');
      return;
    } else {
      emptyState.classList.remove('show');
    }
    
    todoList.innerHTML = filteredTodos.map(todo => `
      <li class="todo-item ${todo.completed ? 'completed' : ''}">
        <input 
          type="checkbox" 
          class="todo-checkbox" 
          ${todo.completed ? 'checked' : ''}
          data-id="${todo.id}"
        >
        <span class="todo-text">${todo.text}</span>
        <div class="todo-actions">
          <button class="todo-btn edit-btn" data-id="${todo.id}">ìˆ˜ì •</button>
          <button class="todo-btn delete-btn" data-id="${todo.id}">ì‚­ì œ</button>
        </div>
      </li>
    `).join('');
  }
  
  getFilteredTodos() {
    switch(this.currentFilter) {
      case 'active':
        return this.todos.filter(t => !t.completed);
      case 'completed':
        return this.todos.filter(t => t.completed);
      default:
        return this.todos;
    }
  }
  
  setFilter(filter, buttonElement) {
    this.currentFilter = filter;
    
    // í•„í„° ë²„íŠ¼ í™œì„±í™” ìƒíƒœ ë³€ê²½
    document.querySelectorAll('.filter-btn').forEach(btn => {
      btn.classList.remove('active');
    });
    buttonElement.classList.add('active');
    
    this.renderTodos();
  }
  
  updateStats() {
    const total = this.todos.length;
    const completed = this.todos.filter(t => t.completed).length;
    const remaining = total - completed;
    
    // ì• ë‹ˆë©”ì´ì…˜ê³¼ í•¨ê»˜ ìˆ«ì ì—…ë°ì´íŠ¸
    this.animateCounter('total-count', total);
    this.animateCounter('completed-count', completed);
    this.animateCounter('remaining-count', remaining);
    
    // í•„í„° ì¹´ìš´íŠ¸ ì—…ë°ì´íŠ¸
    document.getElementById('all-count').textContent = total;
    document.getElementById('active-count').textContent = remaining;
    document.getElementById('completed-filter-count').textContent = completed;
  }
  
  animateCounter(elementId, targetValue) {
    const element = document.getElementById(elementId);
    const currentValue = parseInt(element.textContent) || 0;
    const difference = targetValue - currentValue;
    const increment = difference > 0 ? 1 : -1;
    const steps = Math.abs(difference);
    
    if (steps === 0) return;
    
    let step = 0;
    const timer = setInterval(() => {
      step++;
      const newValue = currentValue + (increment * step);
      element.textContent = newValue;
      
      if (step >= steps) {
        clearInterval(timer);
        // ë³€ê²½ ì™„ë£Œ ì‹œ ì‚´ì§ ê°•ì¡° íš¨ê³¼
        element.style.transform = 'scale(1.1)';
        setTimeout(() => {
          element.style.transform = 'scale(1)';
        }, 200);
      }
    }, 50);
  }
  
  updateCharCount(length) {
    const charCount = document.getElementById('char-count');
    charCount.textContent = length;
    
    // ê¸€ì ìˆ˜ì— ë”°ë¥¸ ìƒ‰ìƒ ë³€ê²½
    if (length > 80) {
      charCount.style.color = '#dc3545';
    } else if (length > 60) {
      charCount.style.color = '#ffc107';
    } else {
      charCount.style.color = '#999';
    }
  }
  
  clearCompleted() {
    const completedCount = this.todos.filter(t => t.completed).length;
    if (completedCount === 0) {
      const button = document.getElementById('clear-completed');
      AnimationUtils.shake(button);
      return;
    }
    
    this.todos = this.todos.filter(t => !t.completed);
    this.renderTodos();
    this.updateStats();
    this.saveToStorage();
  }
  
  clearAll() {
    if (this.todos.length === 0) {
      const button = document.getElementById('clear-all');
      AnimationUtils.shake(button);
      return;
    }
    
    if (confirm('ì •ë§ ëª¨ë“  í• ì¼ì„ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?')) {
      this.todos = [];
      this.renderTodos();
      this.updateStats();
      this.saveToStorage();
    }
  }
  
  saveToStorage() {
    localStorage.setItem('animatedTodos', JSON.stringify(this.todos));
    localStorage.setItem('animatedTodoId', this.todoId.toString());
  }
  
  loadFromStorage() {
    const savedTodos = localStorage.getItem('animatedTodos');
    const savedId = localStorage.getItem('animatedTodoId');
    
    if (savedTodos) {
      this.todos = JSON.parse(savedTodos);
    }
    
    if (savedId) {
      this.todoId = parseInt(savedId);
    }
    
    this.renderTodos();
    this.updateStats();
  }
}

